themeCSS:
/* Dim subgraph panels (clusters) for dark mode */
.cluster rect, .cluster path { fill: #1f2937 !important; fill-opacity: 0.25 !important; stroke: #4b5563 !important; }
/* Cluster titles readable on dim panels */
.cluster > .label text { fill: #e5e7eb !important; }
.cluster > .label span { color: #e5e7eb !important; }
/* Node labels remain light */
.nodeLabel > text { fill: #e5e7eb; }
.label foreignObject div { color: inherit; }
